'async';
const { default: Markdown } = await import('https://esm.sh/react-markdown');
const readme = await (await fetch('https://raw.githubusercontent.com/keenbug/tables/main/README.md')).text();

<Markdown className="simplecss">{readme}</Markdown>

function Counter({ label }) {
  const [count, setCount] = React.useState(0)

  return (
    <div className="flex flex-row space-x-2 p-2 rounded border">
      <div className="text-amber-900">{label}</div>
      <button
        className="rounded bg-amber-600 text-amber-50 px-2"
        onClick={() => setCount(c => c+1)}
        >
        {count}
      </button>
      <button
        className="text-amber-800"
        onClick={() => setCount(0)}
        >
        reset
      </button>

    </div>
  )
}
